if (LOVD_plus) {
    // To prevent a huge diff above, make LOVD+ specific changes to the array *after* it's built.
    foreach ($aColSQL as $nKey => $sInsertSQL) {
        // Find the beginning of field values of an SQL INSERT query
        // INSERT INTO table_name VALUES(...)
        $nIndex = strpos($sInsertSQL, '(');
        if ($nIndex !== false) {
            // Get the string inside brackets VALUES(...)
            $sInsertFields = rtrim(substr($sInsertSQL, $nIndex + 1), ')');

            // Split the string into an array.
            $aValues = str_getcsv($sInsertFields);
            list($sColID, $nOrder, $nWidth, $bHGVS, $bStandard, $bMandatory) = $aValues;
            $sMatch = implode(',', array($bHGVS, $bStandard, $bMandatory)) . ', "';

            $bReplace = true;
            switch ($sColID) {
                case 'Individual/Gender':
                    $bStandard = 1;
                    break;
                case 'Individual/Lab_ID':
                    // Make visible.
                    $sInsertSQL = str_replace('0, 1, 0, 0, NOW()', '1, 1, 0, 0, NOW()', $sInsertSQL);
                    break;
                case 'Individual/Reference':
                    $bHGVS = 0;
                    $bStandard = 0;
                    break;
                case 'Individual/Remarks':
                    // Make invisible.
                    $sInsertSQL = str_replace('1, 1, 1, 0, NOW()', '0, 1, 1, 0, NOW()', $sInsertSQL);
                    break;
                case 'Individual/Remarks_Non_Public':
                    $bStandard = 0;
                    break;
                case 'VariantOnGenome/dbSNP':
                    $bStandard = 1;
                    break;
                case 'VariantOnGenome/Remarks':
                    $bStandard = 1;
                    break;
                case 'VariantOnTranscript/Distance_to_splice_site':
                    $bStandard = 1;
                    break;
                case 'VariantOnTranscript/Exon':
                    $bStandard = 0;
                    break;
                case 'VariantOnTranscript/GVS/Function':
                    $bStandard = 1;
                    break;
                case 'VariantOnTranscript/PolyPhen':
                    $bStandard = 1;
                    // Turn it from a selection list into a free text field.
                    $sInsertSQL = str_replace('|select|1|true|false|false", "benign = Benign\r\npossiblyDamaging = Possibly damaging\r\nprobablyDamaging = Probably damaging\r\nnoPrediction = No prediction"', '|text|30", ""', $sInsertSQL);
                    break;
                default:
                    $bReplace = false;
            }

            if ($bReplace) {
                $aColSQL[$nKey] = str_replace($sMatch, ' ' . implode(', ', array_map('ltrim', array($bHGVS, $bStandard, $bMandatory, '"'))), $sInsertSQL);
            }
        }
